mapper.select:
SELECT a.STANDARD_CATEGORY_ID, a.CATEGORY_NAME, a.`LEVEL`, a.PARENT_ID, b.STANDARD_CATEGORY_ID AS STANDARD_CATEGORY_ID_TWO, b.CATEGORY_NAME AS CATEGORY_NAME_TWO, b.`LEVEL` AS LEVEL_TWO, c.STANDARD_CATEGORY_ID AS STANDARD_CATEGORY_ID_THREE, c.CATEGORY_NAME AS CATEGORY_NAME_THREE, c.`LEVEL` AS LEVEL_THREE FROM T_STANDARD_CATEGORY a LEFT JOIN T_STANDARD_CATEGORY b ON a.STANDARD_CATEGORY_ID = b.PARENT_ID AND b.`STATUS` = #{status, jdbcType=INTEGER} LEFT JOIN T_STANDARD_CATEGORY c ON b.STANDARD_CATEGORY_ID = c.PARENT_ID AND c.`STATUS` = #{status, jdbcType=INTEGER} WHERE a.`STATUS` = #{status, jdbcType=INTEGER}
AND a.PARENT_ID = #{parentId, jdbcType=INTEGER}
ORDER BY a.STANDARD_CATEGORY_ID DESC, b.STANDARD_CATEGORY_ID DESC, c.STANDARD_CATEGORY_ID DESC

SELECT T.* FROM( SELECT DISTINCT IFNULL(g.STANDARD_CATEGORY_ID, IFNULL(t.STANDARD_CATEGORY_ID, c.STANDARD_CATEGORY_ID)) as STANDARD_CATEGORY_ID, IFNULL(g.`LEVEL`, IFNULL(t.`LEVEL`, c.`LEVEL`)) as `LEVEL`, IFNULL(g.SORT, IFNULL(t.SORT, c.SORT)) as SORT, CONCAT(IFNULL(c.CATEGORY_NAME, ""), "->", IFNULL(t.CATEGORY_NAME, ""), "->", IFNULL(g.CATEGORY_NAME, "")) as CATEGORY_NAME, CONCAT(IFNULL(c.PARENT_ID, ""), " ", IFNULL(t.PARENT_ID, ""), " ", IFNULL(g.PARENT_ID, "")) as PARENT_ID FROM T_STANDARD_CATEGORY c LEFT JOIN T_STANDARD_CATEGORY t ON t.PARENT_ID = c.STANDARD_CATEGORY_ID AND t.`STATUS` = 1 LEFT JOIN T_STANDARD_CATEGORY g ON g.PARENT_ID = t.STANDARD_CATEGORY_ID AND g.`STATUS` = 1 WHERE c.`LEVEL` = 1 AND c.STATUS = 1) T WHERE 1 = 1
AND T.CATEGORY_NAME like CONCAT('%',#{categoryName, jdbcType=VARCHAR},'%' )
AND T.STANDARD_CATEGORY_ID = #{standardCategoryId, jdbcType=INTEGER}
AND T.LEVEL = #{level, jdbcType=INTEGER}
order by T.PARENT_ID, T.SORT

SELECT
FROM T_STANDARD_CATEGORY WHERE STATUS = 1 AND PARENT_ID = #{parentId, jdbcType=INTEGER}
ORDER BY STANDARD_CATEGORY_ID, SORT DESC

SELECT a.STANDARD_CATEGORY_ID AS standardCategoryId, IF( c.CATEGORY_NAME = '' OR c.CATEGORY_NAME IS NULL, IF( b.CATEGORY_NAME = '' OR b.CATEGORY_NAME IS NULL, IF(a.CATEGORY_NAME = '' OR a.CATEGORY_NAME IS NULL, '--', a.CATEGORY_NAME), CONCAT(a.CATEGORY_NAME, '>', b.CATEGORY_NAME) ), CONCAT(c.CATEGORY_NAME, '>', b.CATEGORY_NAME, '>', a.CATEGORY_NAME) ) AS categoryName FROM T_STANDARD_CATEGORY a LEFT JOIN T_STANDARD_CATEGORY b ON a.PARENT_ID = b.STANDARD_CATEGORY_ID AND b.`STATUS` = 1 LEFT JOIN T_STANDARD_CATEGORY c ON b.PARENT_ID = c.STANDARD_CATEGORY_ID AND c.`STATUS` = 1 WHERE a.STANDARD_CATEGORY_ID IN
#{first.newStandardCategoryId, jdbcType=INTEGER}
ORDER BY a.STANDARD_CATEGORY_ID DESC
